INSERT INTO book (
    title,
    author,
    isbn13,
    language,
    number_of_pages,
    date_of_publication,
    description
) VALUES
        ('To Kill a Mockingbird', 'Harper Lee', '9780446310789', 'English', 281, '1960-07-11', 'Set in the American South, this beloved novel follows young Scout Finch as her father, lawyer Atticus Finch, defends a black man falsely accused of rape, addressing themes of racial injustice and the loss of innocence.'),
        ('1984', 'George Orwell', '9780451524935', 'English', 328, '1949-06-08', 'A dystopian novel set in a totalitarian society where critical thinking is suppressed and government surveillance is omnipresent, following Winston Smith''s rebellion against the oppressive regime.'),
        ('The Great Gatsby', 'F. Scott Fitzgerald', '9780743273565', 'English', 180, '1925-04-10', 'A tale of the American Dream, wealth, and obsession, told through the eyes of Nick Carraway about the mysterious millionaire Jay Gatsby and his passion for the beautiful Daisy Buchanan.'),
        ('One Hundred Years of Solitude', 'Gabriel García Márquez', '9780060883287', 'Spanish', 417, '1967-05-30', 'A landmark of magical realism, chronicling seven generations of the Buendía family in the mythical town of Macondo, blending fantasy and reality in a complex exploration of time and solitude.'),
        ('Moby Dick', 'Herman Melville', '9780142437247', 'English', 635, '1851-10-18', 'The epic tale of Captain Ahab''s obsessive quest for revenge against the white whale Moby Dick, exploring themes of obsession, nature, and the limits of human knowledge.'),
        ('War and Peace', 'Leo Tolstoy', '9781400079988', 'Russian', 1225, '1869-01-01', 'An epic novel following five aristocratic families in Russia during the Napoleonic Era, examining themes of history, free will, spirituality, and the impact of war on society.'),
        ('Crime and Punishment', 'Fyodor Dostoevsky', '9780141192802', 'Russian', 671, '1866-01-01', 'A psychological thriller following Raskolnikov, a poor student who murders a pawnbroker, exploring themes of morality, redemption, and the psychological torment of guilt.'),
        ('The Catcher in the Rye', 'J.D. Salinger', '9780316769488', 'English', 277, '1951-07-16', 'The story of Holden Caulfield''s three-day journey through New York City, capturing teenage alienation and rebellion while exploring themes of innocence and authenticity.'),
        ('Pride and Prejudice', 'Jane Austen', '9780141439518', 'English', 432, '1813-01-28', 'A witty romance following Elizabeth Bennet and Mr. Darcy as they overcome their pride and prejudices against a backdrop of class, marriage, and social expectations in Regency England.'),
        ('The Hobbit', 'J.R.R. Tolkien', '9780547928227', 'English', 310, '1937-09-21', 'The adventure of Bilbo Baggins, who joins a company of dwarves and the wizard Gandalf on a quest to reclaim a treasure guarded by the dragon Smaug, setting the stage for The Lord of the Rings.'),
        ('Ulysses', 'James Joyce', '9780679722762', 'English', 730, '1922-02-02', 'A modernist masterpiece following Leopold Bloom through Dublin on June 16, 1904, paralleling Homer''s Odyssey while revolutionizing stream-of-consciousness narrative.'),
        ('Brave New World', 'Aldous Huxley', '9780060850524', 'English', 311, '1932-01-01', 'A dystopian vision of a future society where technological progress and social stability are maintained through the sacrifice of individual freedom, art, and science.'),
        ('The Brothers Karamazov', 'Fyodor Dostoevsky', '9780374528379', 'Russian', 796, '1880-01-01', 'A philosophical novel exploring faith, doubt, and morality through the story of the Karamazov brothers and their father''s murder, examining the nature of God, free will, and human responsibility.'),
        ('Anna Karenina', 'Leo Tolstoy', '9780143035008', 'Russian', 864, '1877-01-01', 'The tragic story of married aristocrat Anna Karenina and her affair with Count Vronsky, set against the backdrop of Russian high society, exploring themes of love, marriage, and social norms.'),
        ('Madame Bovary', 'Gustave Flaubert', '9780140449129', 'French', 329, '1856-12-15', 'The story of Emma Bovary, a doctor''s wife who pursues her romantic fantasies through affairs and living beyond her means, leading to her tragic downfall.'),
        ('The Picture of Dorian Gray', 'Oscar Wilde', '9780141439570', 'English', 254, '1890-07-01', 'A Gothic tale about a beautiful young man who trades his soul for eternal youth while his portrait ages and records his sins, exploring themes of aestheticism, morality, and corruption.'),
        ('Frankenstein', 'Mary Shelley', '9780141439471', 'English', 280, '1818-01-01', 'The story of Victor Frankenstein and his creation of a monster, exploring themes of ambition, scientific responsibility, and the nature of humanity.'),
        ('Don Quixote', 'Miguel de Cervantes', '9780142437230', 'Spanish', 1072, '1605-01-16', 'The adventures of Alonso Quixano, who becomes a knight-errant Don Quixote, and his squire Sancho Panza, blending comedy with profound observations about literature and life.'),
        ('Jane Eyre', 'Charlotte Brontë', '9780141441146', 'English', 500, '1847-10-16', 'The story of orphan Jane Eyre and her journey from a harsh childhood to becoming a governess at Thornfield Hall, where she falls in love with the mysterious Mr. Rochester.'),
        ('The Divine Comedy', 'Dante Alighieri', '9780142437223', 'Italian', 798, '1320-01-01', 'An epic poem following Dante''s journey through Hell, Purgatory, and Paradise, serving as an allegory for the soul''s journey toward God.'),
        ('Les Misérables', 'Victor Hugo', '9780451419439', 'French', 1463, '1862-01-01', 'A sweeping epic about redemption, justice, and the struggles of ex-convict Jean Valjean in post-revolutionary France.'),
        ('Wuthering Heights', 'Emily Brontë', '9780141439556', 'English', 416, '1847-12-01', 'A haunting tale of passion and revenge set on the Yorkshire moors, centering on the doomed love between Heathcliff and Catherine Earnshaw.'),
        ('The Stranger', 'Albert Camus', '9780679720201', 'French', 123, '1942-01-01', 'A philosophical novel about absurdism, following Meursault, a man who kills a stranger and reflects on life, death, and meaning while awaiting execution.'),
        ('The Trial', 'Franz Kafka', '9780805209990', 'German', 255, '1925-01-01', 'A nightmarish novel in which Josef K. is arrested and prosecuted by a remote authority for an unspecified crime, capturing existential dread and bureaucratic absurdity.'),
        ('A Tale of Two Cities', 'Charles Dickens', '9780141439600', 'English', 489, '1859-04-30', 'Set during the French Revolution, this novel tells the story of love, sacrifice, and resurrection through the intertwined lives of Charles Darnay and Sydney Carton.'),
        ('The Count of Monte Cristo', 'Alexandre Dumas', '9780140449266', 'French', 1276, '1844-08-28', 'A classic tale of betrayal, revenge, and redemption as Edmond Dantès escapes prison, uncovers a treasure, and transforms into the mysterious Count of Monte Cristo.'),
        ('Dracula', 'Bram Stoker', '9780141439846', 'English', 418, '1897-05-26', 'The Gothic horror novel that introduced Count Dracula and established many conventions of vampire fiction.'),
        ('The Metamorphosis', 'Franz Kafka', '9780553213690', 'German', 201, '1915-01-01', 'The story of Gregor Samsa, who wakes up one day transformed into a giant insect, examining themes of alienation, identity, and existential guilt.'),
        ('Fahrenheit 451', 'Ray Bradbury', '9781451673319', 'English', 249, '1953-10-19', 'In a future where books are outlawed and burned, fireman Guy Montag begins to question his role and seek knowledge in a world dominated by mass media and censorship.'),
        ('The Sound and the Fury', 'William Faulkner', '9780679732242', 'English', 326, '1929-10-07', 'A complex Southern Gothic novel told from multiple perspectives, depicting the decline of the Compson family and exploring time, memory, and consciousness.'),
        ('Lolita', 'Vladimir Nabokov', '9780679723165', 'English', 336, '1955-09-15', 'A controversial novel narrated by Humbert Humbert, who becomes obsessed with a twelve-year-old girl named Dolores Haze, examining obsession and manipulation.'),
        ('Beloved', 'Toni Morrison', '9781400033416', 'English', 324, '1987-09-02', 'The story of Sethe, a former enslaved woman haunted by the ghost of her dead daughter, dealing with memory, trauma, and the legacy of slavery.'),
        ('A Clockwork Orange', 'Anthony Burgess', '9780393312836', 'English', 192, '1962-01-01', 'A dystopian novel exploring free will, violence, and state control through the eyes of teenage delinquent Alex and his journey through rehabilitation.'),
        ('The Sun Also Rises', 'Ernest Hemingway', '9780743297332', 'English', 251, '1926-10-22', 'A novel about the disillusioned post-WWI generation, following expatriates from Paris to Pamplona during the running of the bulls and exploring themes of love, masculinity, and the Lost Generation.'),
        ('The Grapes of Wrath', 'John Steinbeck', '9780143039433', 'English', 464, '1939-04-14', 'During the Great Depression, the Joad family travels from Oklahoma to California in search of a better life, exposing social injustices and economic hardship.'),
        ('Catch-22', 'Joseph Heller', '9781451626650', 'English', 453, '1961-11-10', 'A satirical war novel following bombardier Yossarian and the absurdity of military bureaucracy, where trying to avoid combat is a sign of sanity and therefore disqualifies you from being excused.'),
        ('Slaughterhouse-Five', 'Kurt Vonnegut', '9780385333849', 'English', 275, '1969-03-31', 'The story of Billy Pilgrim, who becomes "unstuck in time" and experiences moments of his life, including his abduction by aliens and survival of the Dresden bombing, as a critique of war.'),
        ('Heart of Darkness', 'Joseph Conrad', '9780486264646', 'English', 80, '1899-01-01', 'A novella exploring imperialism and human nature as Marlow travels up the Congo River to find the enigmatic ivory trader Kurtz.'),
        ('The Old Man and the Sea', 'Ernest Hemingway', '9780684801223', 'English', 127, '1952-09-01', 'An aging Cuban fisherman struggles with a giant marlin in this tale of endurance, pride, and the human spirit.');



